// ===============================================
// ARROW FUNCTIONS
// ===============================================
(function () {
	var numbers = [1, 2, 3, 4, 5];
	var doubleNumbers = numbers.map(function (number) {
		return number * 2;
	});
	console.log('ARROW FUNCTIONS: Double Numbers: ' + doubleNumbers);
	var numbersTotal = numbers.reduce(function (prev, current) {
		return prev + current;
	});
})();

